import React, { useState } from 'react';
import Modal from './Modal';
import './WeeklySpecials.css';

const WeeklySpecials = ({ onOrderNow }) => {
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [gifSrc, setGifSrc] = useState('');

  const handleOrderNow = () => {
    setModalMessage("Your order has been confirmed!");
    setGifSrc("/images/confirmed.gif"); // Add a confirmation GIF
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <>
      <section className="weekly-specials">
        <h2>This Week's Specials!</h2>
        <div className="specials-grid">
          <div className="special-item">
            <img src="/images/greek-salad.jpg" alt="Greek Salad" />
            <h3>Greek Salad</h3>
            <p>The famous Greek salad of crispy lettuce, peppers, olives, and our Chicago-style feta cheese, garnished with crunchy garlic and rosemary croutons.</p>
            <button onClick={handleOrderNow}>Order Now</button>
          </div>
          <div className="special-item">
            <img src="/images/bruschetta.jpg" alt="Bruschetta" />
            <h3>Bruschetta</h3>
            <p>Our Bruschetta is made from grilled bread that has been smeared with garlic and seasoned with salt and olive oil.</p>
            <button onClick={handleOrderNow}>Order Now</button>
          </div>
          <div className="special-item">
            <img src="/images/lemon-dessert.jpg" alt="Lemon Dessert" />
            <h3>Lemon Dessert</h3>
            <p>This comes straight from grandma's recipe book. Every last ingredient has been sourced and is as authentic as can be imagined.</p>
            <button onClick={handleOrderNow}>Order Now</button>
          </div>
        </div>
      </section>
      <Modal 
        show={showModal} 
        onClose={closeModal} 
        message={modalMessage} 
        gifSrc={gifSrc} 
      />
    </>
  );
};

export default WeeklySpecials;
